root = StrictPartialOrder(nodes=[initial_inquiry_to_document_review, document_review_to_historical_research, historical_research_to_material_sampling, material_sampling_to_forensic_testing, forensic_testing_to_ownership_audit, ownership_audit_to_legal_verification, legal_verification_to_ethical_screening, ethical_screening_to_expert_consultation, expert_consultation_to_cultural_assessment, cultural_assessment_to_condition_survey, condition_survey_to_provenance_mapping, provenance_mapping_to_risk_analysis, risk_analysis_to_report_compilation, report_compilation_to_acquisition_approval, acquisition_approval_to_repatriation_review, repatriation_review_to_archival_storage])
