import pm4py
from pm4py.objects.powl.obj import StrictPartialOrder, OperatorPOWL, Transition, SilentTransition
from pm4py.objects.process_tree.obj import Operator

# Define the activities
approve_report = Transition(label='Approve report')
archive_report = Transition(label='Archive report')
check_regulatory_updates = Transition(label='Check regulatory updates')
close_audit_process = Transition(label='Close audit process')
compile_audit_report = Transition(label='Compile audit report')
complete_risk_assessment_mitigation = Transition(label='Complete risk assessment/mitigation')
conduct_data_analysis = Transition(label='Conduct data analysis')
conduct_interviews = Transition(label='Conduct interviews')
distribute_final_report = Transition(label='Distribute final report')
evaluate_compliance_risks = Transition(label='Evaluate compliance risks')
evaluate_financial_risks = Transition(label='Evaluate financial risks')
evaluate_operational_risks = Transition(label='Evaluate operational risks')
gather_necessary_documents = Transition(label='Gather necessary documents')
launch_detailed_investigation = Transition(label='Launch detailed investigation')
perform_site_visits = Transition(label='Perform site visits')
prepare_financial_statements = Transition(label='Prepare financial statements')
receive_clarifications = Transition(label='Receive clarifications')
request_clarifications_of_discrepancies = Transition(label='Request clarifications of discrepancies')
review_report_by_audit_director = Transition(label='Review report by audit director')
review_submission = Transition(label='Review submission')
send_notification = Transition(label='Send notification')
send_report_for_revision = Transition(label='Send report for revision')
submit_documents = Transition(label='Submit documents')
update_and_resubmit_report = Transition(label='Update and resubmit report')

# Define the workflow
send_notification_to_regional_office = OperatorPOWL(operator=Operator.SILENT, children=[send_notification])
prepare_financial_statements_and_gather_necessary_documents = OperatorPOWL(operator=Operator.SILENT, children=[prepare_financial_statements, gather_necessary_documents])
check_regulatory_updates_and_concurrent_with_preparation = OperatorPOWL(operator=Operator.XOR, children=[check_regulatory_updates, OperatorPOWL(operator=Operator.SILENT, children=[prepare_financial_statements_and_gather_necessary_documents])])
submit_documents_and_review = OperatorPOWL(operator=Operator.SILENT, children=[submit_documents, review_submission])
conduct_data_analysis_and_interviews_and_site_visits = OperatorPOWL(operator=Operator.SILENT, children=[conduct_data_analysis, conduct_interviews, perform_site_visits])
complete_risk_assessment_mitigation_and_concurrent_with_data_analysis = OperatorPOWL(operator=Operator.XOR, children=[complete_risk_assessment_mitigation, OperatorPOWL(operator=Operator.SILENT, children=[conduct_data_analysis_and_interviews_and_site_visits])])
review_report_by_audit_director_and_send_report_for_revision = OperatorPOWL(operator=Operator.XOR, children=[review_report_by_audit_director, OperatorPOWL(operator=Operator.SILENT, children=[send_report_for_revision])])
launch_detailed_investigation_and_concurrent_with_review = OperatorPOWL(operator=Operator.XOR, children=[launch_detailed_investigation, OperatorPOWL(operator=Operator.SILENT, children=[review_report_by_audit_director_and_send_report_for_revision])])
gather_necessary_documents_and_concurrent_with_preparation = OperatorPOWL(operator=Operator.XOR, children=[gather_necessary_documents, OperatorPOWL(operator=Operator.SILENT, children=[prepare_financial_statements_and_gather_necessary_documents])])
