import pm4py
from pm4py.objects.powl.obj import StrictPartialOrder, OperatorPOWL, Transition, SilentTransition
from pm4py.objects.process_tree.obj import Operator

# Define the transitions (activities)
site_survey = Transition(label='Site Survey')
design_layout = Transition(label='Design Layout')
material_sourcing = Transition(label='Material Sourcing')
system_assembly = Transition(label='System Assembly')
sensor_install = Transition(label='Sensor Install')
nutrient_prep = Transition(label='Nutrient Prep')
water_testing = Transition(label='Water Testing')
climate_setup = Transition(label='Climate Setup')
data_integration = Transition(label='Data Integration')
growth_monitoring = Transition(label='Growth Monitoring')
pest_control = Transition(label='Pest Control')
waste_sorting = Transition(label='Waste Sorting')
harvest_plan = Transition(label='Harvest Plan')
produce_pack = Transition(label='Produce Pack')
energy_audit = Transition(label='Energy Audit')
community_setup = Transition(label='Community Setup')

# Define the silent transition for skip
skip = SilentTransition()

# Define the XOR transition for growth monitoring and pest control
xor_growth_pest = OperatorPOWL(operator=Operator.XOR, children=[growth_monitoring, pest_control])

# Define the loop for nutrient prep and water testing
loop_nutrient_water = OperatorPOWL(operator=Operator.LOOP, children=[nutrient_prep, water_testing])

# Define the XOR transition for waste sorting and community setup
xor_waste_community = OperatorPOWL(operator=Operator.XOR, children=[waste_sorting, community_setup])

# Define the XOR transition for data integration and climate setup
xor_data_climate = OperatorPOWL(operator=Operator.XOR, children=[data_integration, climate_setup])

# Define the XOR transition for energy audit and produce pack
xor_energy_produce = OperatorPOWL(operator=Operator.XOR, children=[energy_audit, produce_pack])

# Define the XOR transition for system assembly and sensor install
xor_system_sensor = OperatorPOWL(operator=Operator.XOR, children=[system_assembly, sensor_install])

# Define the XOR transition for material sourcing and design layout
xor_material_design = OperatorPOWL(operator=Operator.XOR, children=[material_sourcing, design_layout])

